import React, { Component } from "react";
import { connect } from "react-redux";
import { BrowserRouter, Route } from "react-router-dom";
import { NavLink } from "react-router-dom";
import ProductForm from "./products/ProductForm";
import DataService from "../services/dataService";
import { setUser } from "../store/action/actionBundle";
class Nav extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoggedIn: false
    };
  }

  handleLogout = () => {
    this.props.setUser(null);
  };

  render() {
    const loggedUser = DataService.getLoggedUser();
    console.log("logged user render: ", loggedUser);
    return (
      <nav class="navbar navbar-expand-lg navbar-secondary">
        <a class="navbar-brand" href="/">
          <NavLink className="nav-link" to="/products">
            <h6 className="text-dark">Amazon clone </h6>
          </NavLink>
        </a>
        <button
          class="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span class="navbar-toggler-icon" />
        </button>

        <div class="collapse navbar-collapse" id="navbarSupportedContent">
          <ul class="navbar-nav mr-auto">
            <li class="nav-item">
              <NavLink className="nav-link" to="/products">
                <h5 className="text-light">Products </h5>
              </NavLink>
            </li>

            <li class="nav-item">
              <NavLink className="nav-link" to="/about">
                <h5 className="text-light">About </h5>
              </NavLink>
            </li>
          </ul>
          <form class="form-inline my-2 my-lg-0">
            <div class="mx-5">
              <ul class="ml-auto navbar-nav ml-auto">
                {!this.props.user && (
                  <li class="nav-item mx-2">
                    <NavLink className="nav-link btn  btn-lg" to="/logIn">
                      <h5 className="text-light">Login </h5>
                    </NavLink>
                  </li>
                )}

                {this.props.user && (
                  <li class="nav-item mx-2">
                    <NavLink className="nav-link btn  btn-lg" to="/logIn">
                      <h5 className="text-light">Logout </h5>
                    </NavLink>
                  </li>
                )}
                {!this.props.user && (
                  <li class="nav-item mx-2">
                    <NavLink className="nav-link btn  btn-lg" to="/register">
                      <h5 className="text-light">Signup </h5>
                    </NavLink>
                  </li>
                )}
                <li class="nav-item mx-2 d-flex flex-row">
                  <div>
                    <NavLink className="nav-link btn  btn-lg" to="/cart">
                      <div class="d-flex flex-row justify-content-center align-items-center">
                        <div class="p-2">
                          {" "}
                          <span class="badge badge-light text-primary">
                            {this.props.cartItems.length}{" "}
                          </span>{" "}
                        </div>
                        <div class="p-2">
                          {" "}
                          <h5 className="text-light">Cart </h5>
                        </div>
                      </div>
                    </NavLink>
                  </div>
                </li>
              </ul>
            </div>
          </form>
        </div>
      </nav>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.rootReducer.user,
    cartItems: state.rootReducer.cartItems
  };
};

const mapDispatchToProps = dispatch => {
  return {
    setUser: user => dispatch(setUser(user))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Nav);
