import React, { Component } from "react";
import { connect } from "react-redux";
import { Link, NavLink } from "react-router-dom";
import { setProductToCart } from "../../store/action/actionBundle";
import StarRatings from "react-star-ratings";
import API from "../../services/api";

class ProductList extends Component {
  delete = id => {
    this.props.delete(id);
  };
  constructor(props) {
    super(props);
    this.state = {
      rating: 0
    };
  }

  changeRating = (newRating, id) => {
    console.log("id: " + id);
    console.log("new rating: ", newRating);
    API.setRating(id, newRating).then(() => {
      this.props.getAllProducts();
      this.setState({
        rating: newRating
      });
    });
  };

  render() {
    const imageUrl = "https://via.placeholder.com/100";
    console.log("this.products: ", this.props.products);
    return (
      <div className="scrolling-wrapper p-3">
        {this.props.products.map((product, key) => {
          const editLink = "/editForm/" + product.id;
          const id = product.id;
          return (
            <div className="card my-3 h-100">
              <img
                className="card-img-top"
                src={product.imagePath}
                alt="Card image cap"
                style={{
                  height: 200,
                  aspectRation: 16 / 9,
                  width: "100%",
                  resizeMode: "contain"
                }}
              />

              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text text-dark">{product.ratingInfo}</p>
                <div>
                  {product.rate == 0 && (
                    <StarRatings
                      rating={product.rate}
                      starRatedColor="blue"
                      isAggregateRating={true}
                      changeRating={newRating =>
                        this.changeRating(newRating, product.id)
                      }
                      numberOfStars={5}
                      starDimension={30}
                      name="rating"
                    />
                  )}
                </div>

                <div className="my-3 ">
                  <button
                    onClick={() => this.props.setProductToCart(product)}
                    href="#"
                    className="btn btn-primary"
                  >
                    <h3>
                      <i class="fas fa-cart-plus text-primary" />
                    </h3>
                  </button>
                  {this.props.user && this.props.user.role === "Merchant" && (
                    <Link to={editLink}>
                      <a
                        onClick={this.navigateEditForm}
                        className="btn btn-outline-light btn-lg mr-5 my-3 "
                      >
                        Edit{" "}
                      </a>
                    </Link>
                  )}
                  {this.props.user && this.props.user.role === "Merchant" && (
                    <button
                      onClick={() => this.delete(id)}
                      className="btn btn-primary"
                    >
                      delete
                    </button>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    cartItems: state.rootReducer.cartItems,
    user: state.rootReducer.user
  };
};

const mapDispatchToProps = dispatch => {
  return {
    setProductToCart: product => dispatch(setProductToCart(product))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProductList);

