import React, { Component } from "react";
import { connect } from "react-redux";
import ProductList from "./ProductList";
import { Link, NavLink } from "react-router-dom";
import SearchBar from "react-js-search";
import API from "../../services/api";
import { getProducts } from "../../store/action/actions";

class Products extends Component {
  state = {
    searchedProducts: []
  };
  delete = id => {
    API.deleteProduct(id)
      .then(() => this.props.getAllProducts())
      .catch(err => console.log(err));
  };

  componentWillMount() {
    this.props.getAllProducts();
  }

  componentDidMount = () => {
    this.setState({ searchProducts: this.props.products });
  };

  searchProduct = event => {
    console.log("inside serach");
    let updatedList = this.state.searchedProducts;
    updatedList = updatedList.filter(item => {
      return (
        item.name.toLowerCase().search(event.target.value.toLowerCase()) !== -1
      );
    });
    this.setState({ searchProducts: updatedList }, () =>
      console.log("result: ", this.state.searchedProducts)
    );
  };

  render() {
    const shoes = this.props.products.filter(
      products => products.category === "shoes"
    );
    const mobiles = this.props.products.filter(
      products => products.category === "mobile"
    );

    return (
      <div className="body">
        <div className=" btn-container">
          {this.props.user && this.props.user.role == "ADMIN" && (
            <Link to="/productForm">
              <button
                onClick={this.navigateProductForm}
                className="btn btn-success btn-lg mr-5 my-3 "
              >
                Add Product
              </button>
            </Link>
          )}
        </div>

        <div className="col-sm-6 my-2">
          <div className="input-group">
            <input
              onChange={this.searchProduct}
              class="form-control"
              type="search"
              placeholder="Search product by name "
            />
            <div class="input-group-append">
              <button
                class="btn btn-outline-primary my-2 my-sm-0"
                type="submit"
              >
                Search
              </button>
            </div>
          </div>
        </div>

        <ProductList
          delete={this.delete}
          products={shoes}
          getAllProducts={this.props.getAllProducts}
        />
        <ProductList
          delete={this.delete}
          products={mobiles}
          getAllProducts={this.props.getAllProducts}
        />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    products: state.rootReducer.products,
    user: state.rootReducer.user
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getAllProducts: () => dispatch(getProducts())
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Products);
