import React, { Component } from "react";
import { connect } from "react-redux";
import { BrowserRouter, Route } from "react-router-dom";
import { NavLink } from "react-router-dom";
import ProductForm from "./products/ProductForm";
import DataService from "../services/dataService";
import { setUser } from "../store/action/actionBundle";
class Nav extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoggedIn: false
    };
  }

  handleLogout = () => {
    this.props.setUser(null);
  };

  render() {
    const loggedUser = DataService.getLoggedUser();
    console.log("logged user render: ", loggedUser);
    return (
      <div className="navbar">
        <div className="col-md-1  navlist">
          <a className="logo">logo</a>
        </div>
        <div className="col-md-9 justuft-content-around ">
          <NavLink className="mx-2" exact to="/">
            Home
          </NavLink>
          <NavLink className="mx-2" to="/products">
            Products
          </NavLink>
          <NavLink className="mx-2" to="/about">
            About
          </NavLink>
          <NavLink className="mx-2" to="/register">
            register
          </NavLink>

          {this.props.user && (
            <NavLink className="mx-2" to="/orderHistory">
              Order history
            </NavLink>
          )}

          {this.props.user && (
            <NavLink className="mx-2" to="/userProfile">
              Profile
            </NavLink>
          )}
        </div>

        <div className="col-md-1 ml-auto ">
          <input className="input" type="text" name="FirstName" />
        </div>
        {!this.props.user && (
          <div className="col-md-1 ml-auto ">
            <NavLink className="logIn" exact to="/logIn">
              logIn
            </NavLink>
          </div>
        )}

        <div className="col-md-1 ml-auto ">
          <NavLink className="logIn" exact to="/cart">
            <span class="badge badge-light">
              {" "}
              {this.props.cartItems.length}{" "}
            </span>
            Cart
          </NavLink>
        </div>

        {this.props.user && (
          <div className="col-md-1 ml-auto ">
            <button
              onClick={() => this.handleLogout()}
              className="out"
              exact
              to="/logIn"
            >
              Logg ut
            </button>
          </div>
        )}

        <nav class="navbar navbar-expand-lg navbar-light bg-light">
          <a class="navbar-brand" href="#">
            <h6 className="text-dark">Amazon clone </h6>
          </a>
          <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon" />
          </button>

          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav mr-auto">
              <li class="nav-item active">
                <NavLink className="nav-link" exact to="/">
                  <h6 className="text-dark">Home </h6>
                </NavLink>
              </li>
              <li class="nav-item">
                <NavLink className="nav-link" to="/products">
                  <h6 className="text-dark">Products </h6>
                </NavLink>
              </li>

              <li class="nav-item">
                <NavLink className="nav-link" to="/about">
                  <h6 className="text-dark">About </h6>
                </NavLink>
              </li>
            </ul>
            <form class="form-inline my-2 my-lg-0">
              <button class="btn btn-light my-2 my-sm-0 " type="submit">
                <NavLink className="nav-link" exact to="/logIn">
                  <h6 className="text-dark">Login </h6>
                </NavLink>
              </button>
              <button class="btn btn-light my-2 my-sm-0 " type="submit">
                <NavLink className="nav-link" exact to="/logIn">
                  <h6 className="text-dark">Logout </h6>
                </NavLink>
              </button>
              <div class="btn btn-light my-2 my-sm-0 " type="submit">
                <NavLink className="nav-link" exact to="/cart">
                  {this.props.cartItems.length}{" "}
                  <h6 className="text-dark">
                    {" "}
                    <span class="badge badge-light"> </span>Cart{" "}
                  </h6>
                </NavLink>
              </div>
            </form>
          </div>
        </nav>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.rootReducer.user,
    cartItems: state.rootReducer.cartItems
  };
};

const mapDispatchToProps = dispatch => {
  return {
    setUser: user => dispatch(setUser(user))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Nav);
