import React, { Component } from "react";
import { connect } from "react-redux";
import API from "../../services/api";
import { getUsers } from "../../store/action/actions";

class AdminPage extends Component {
  deleteAccount = () => {
    API.deleteUser(this.props.user.id).then(() => {
      this.setState({ deleteConfirmation: true });

      setTimeout(() => {
        this.setState({ deleteConfirmation: false }, () => {
          this.props.setUser(null);

          this.props.history.push("/adminPage");
        });
      }, 3000);
    });
  };

  profileChange = (event, key) => {
    console.log("value you are typing: ", event.target.value);
    console.log("updating: ", event.target.id);
    this.setState({
      [key]: event.target.value
    });
  };

  validateForm() {
    return (
      this.state.email.length > 0 &&
      this.state.password.length > 0 &&
      this.state.password === this.state.confirmPassword
    );
  }

  profileSubmit = async event => {
    event.preventDefault();

    let updatedUser = this.props.user;
    updatedUser["firstName"] = this.state.password;

    let updateUser = {
      password: this.state.password
    };

    API.updateUser(updatedUser).then(() => {
      this.setState({ updatConfirmation: true });
      setTimeout(() => {
        this.setState({ updatConfirmation: false });
      }, 4000);
    });
  };

  componentWillMount() {
    this.props.getAllUsers();
  }
  render() {
    return (
      <div className="container">
        <div className="col-sm-6 mx-auto my-5">
          <table class="table">
            <thead>
              <tr>
                <th scope="col">Id</th>
                <th scope="col">Name</th>
                <th scope="col">Rols</th>
              </tr>
            </thead>
            <tbody>
              {this.props.user.map((user, key) => (
                <tr key={key}>
                  <th scope="row">{user.id}</th>
                  <td>{user.firstName}</td>
                  <td>{user.last}</td>
                  <td>{user.roles}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="col-sm-6 mx-auto">
            <button className="btn btn-success btn-block ">CHECKOUT</button>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    user: state.rootReducer.user,
    users: state.rootReducer.users
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getAllUsers: users => dispatch(getUsers(users))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AdminPage);
