import React, { Component } from "react";
import { connect } from "react-redux";
import { Link, NavLink } from "react-router-dom";

import API from "../../services/api";
import { getProducts } from "../../store/action/actions";
//import { getMyProducts } from "../../store/action/actions";

class MyProducts extends Component {
  delete = id => {
    API.deleteProduct(id)
      .then(() => this.props.getMyProduct())
      .catch(err => console.log(err));
  };

  componentWillMount() {
    this.props.getAllProducts();
    // console.log("this.props.products", this.props.products);
  }
  componentDidMount = () => {
    console.log("this.props.products", this.props.products);
  };

  render() {
    const shoes = this.props.MyProducts.filter(
      myProducts => MyProducts.category === "shoes"
    );
    const mobiles = this.props.MyProducts.filter(
      Myroducts => MyProducts.category === "mobile"
    );
  }
  render() {
    return (
      <div>
        {" "}
        <div className="body">
          <div className=" btn-container">
            {this.props.user && this.props.user.role == "MERCHANT" && (
              <Link to="/productForm">
                <button
                  onClick={this.navigateProductForm}
                  className="btn btn-success btn-lg mr-5 my-3 "
                >
                  Add Product
                </button>
              </Link>
            )}
          </div>
          {/* <Test title="ادريس " />
          <ProductList delete={this.delete} products={shoes} />
          <ProductList delete={this.delete} products={mobiles} /> */}
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    myProducts: state.rootReducer.myProducts,
    user: state.rootReducer.user,
    products: state.rootReducer.products
  };
};

const mapDispatchToProps = dispatch => {
  return {
    // getMyProducts: () => dispatch(getMyProducts())
    getAllProducts: () => dispatch(getProducts())
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MyProducts);
